import React from 'react';
import AlertPropsType from '../types/AlertPropsType';
import AlertType from '../types/AlertType';
import './Alert.css';

const BEEP_ENABLED : boolean = false;     // Disabled as it was annoying

/**
 * React functional component for each Alert
 */
const alert : React.FC<AlertPropsType> = function Alert(props) {
    const alert : AlertType = props.item;
    const classname : string = 'alert ' + getClassName(alert.pctChange);
    const arrowClass: string = (alert.pctChange < 0) ? 'rotateRight90' : 'rotateLeft90';

    return  <li className={classname}>
                <a href={alert.url} target="_blank">
                    <div className='smallFont'>{alert.time}</div>
                    <img src={alert.logoUrl}/>
                    <div>
                        <div>{alert.symbol}</div>
                        <div className='smallFont'>{alert.name}</div>
                    </div>
                    <div className='smallFont'>vol: &#8383;{alert.volume}</div>
                    <div className='smallFont'>
                        {alert.oldPrice}
                        <div className={arrowClass}> &#x279C; </div>
                        {alert.newPrice}
                    </div>
                    <div>{alert.pctChange}%</div>
                </a>
            </li>;
}

/**
 * Determine the colour for the component, depending on the percentage change in price
 */
function getClassName(pctChange : number) : string {
    if (pctChange < -20) {
        beep();
        return 'veryLargeDrop';
    } else if (pctChange < -10) {
        beep();
        return 'largeDrop';
    } else if (pctChange < -6) {
        return 'mediumDrop';
    } else if (pctChange < -3) {
        return 'smallDrop';
    } else if (pctChange == 0) {
        return 'nochange';
    } else if (pctChange > 10) {
        return 'largeIncrease';
    } else {
        return 'increase';
    }
}

/**
 * Play a short beep sound
 */
function beep() : void {
    if (BEEP_ENABLED) {
        var snd = new Audio("data:audio/wav;base64,");
        snd.play();
    }
}

export default React.memo(alert);
